void update_vsc_task_status(unsigned char ctrl, unsigned int status)
{//ctrl: 1  set    0: clear
    if(ctrl)
    {
        vsc_task_status = vsc_task_status | status;
    }
    else
    {
        vsc_task_status = vsc_task_status & (~status);
    }
}


