					linebuffer[i] =( (srclineptr[i]&0x000000ff)<<24 )|( (srclineptr[i]&0x0000ff00)<<8 )| ( (srclineptr[i]&0x00ff0000)>>8 )|( (srclineptr[i]&0xff000000)>>24 );
